App.SearchBarComponent = Ember.Component.extend({
    DISPLAY_TEXT_S0: "Search",
    DISPLAY_TEXT_S1: "Search by people name, skills, location",
    STATE_S0: 0,
    STATE_S1: 1,
    STATE_S2: 2,
    STATE_S3: 3,
    controller: null,
    classNames: [ "navbar-form", "navbar-right", "search-container" ],
    classNameBindings: [ "isExpanded:expanded" ],
    hasFocus: !1,
    isCollapsed: function() {
        var a = this.get("searchState");
        return a == this.STATE_S0 || a == this.STATE_S3;
    }.property("searchState"),
    isExpanded: function() {
        var a = this.get("searchState");
        return a == this.STATE_S1 || a == this.STATE_S2;
    }.property("searchState"),
    isExpandedDirty: function() {
        var a = this.get("searchState");
        return a == this.STATE_S2;
    }.property("searchState"),
    searchState: function() {
        var a = this.get("hasFocus"), b = this.get("searchText");
        return a || "" !== b ? a && "" === b ? this.STATE_S1 : a && "" !== b ? this.STATE_S2 : this.STATE_S3 : this.STATE_S0;
    }.property("hasFocus", "searchText"),
    searchText: "",
    placeholderText: function() {
        var a = this.get("hasFocus");
        return a ? this.DISPLAY_TEXT_S1 : this.DISPLAY_TEXT_S0;
    }.property("hasFocus"),
    searchClear: function() {
        this.set("searchText", "");
    },
    searchExecute: function() {
        alert("Performing search");
    },
    actions: {
        searchClear: function() {
            this.set("searchText", "");
        },
        searchEnter: function() {
            this.set("hasFocus", !0);
        },
        searchExecute: function() {
            alert("Performing search");
        },
        searchExit: function() {
            this.set("hasFocus", !1);
        }
    }
});